import React, { useCallback, useState } from "react";
import PropTypes from "prop-types";
const PostImages = ({ images }) => {
  const [showImagesZoom, setSHowImagesZoom] = useState(false);
  {
    const onZoom = useCallback(() => {
      setSHowImagesZoom(true);
    }, []);

    if (images.length === 1) {
      return (
        <>
          <img
            role="presentation"
            src={images[0].src}
            alt={images[0].src}
            onClick={onZoom}
          />
        </>
      );
    }
    if (images.length === 2) {
      return (
        <>
          <img
            role="presentation"
            src={images[0].src}
            alt={images[0].src}
            onClick={onZoom}
          />
          <img
            role="presentation"
            src={images[1].src}
            alt={images[1].src}
            onClick={onZoom}
          />
        </>
      );
    }
    return (
      <div>
        <img
          role="presentation"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        />
        <div role="presentation" style={{display}} ></div>
      </div>
    );
  }
};

PostImages.propTypes = {
  images: PropTypes.arrayOf(PropTypes.object),
};

export default PostImages;
