import { Card, Popover } from "antd";
import React from "react";
import { HeartOutlined, MessageOutlined, RetweetOutlinded } from "@ant-design/icons";
const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages Images={post.Images} />}
        actions={[<RetweetOutlinded />],
    <HeartOutlined/>,
<MessageOutlined/>,
<Popover/>
}
      >
        <Image />
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
