import { Card, Popover } from "antd";
import React from "react";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card cover={post.Images[0] && <PostImages Images={post.Images} />}
      actions={[
        <RetweetOutlined/>,
        <HeartOutlinde/>,
        <MessageOutlined/>,
        <Popover>
           
        </Popover>
      ]}>
        <Image />
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
