export const initialState = {
  isLoggingIn: false,
  isLoggedIn: false,
  isLoggingOut: false,
  me: null,
  signUpData: {},
  loginData: {},
};
export const loginAction = (data) => {
  return { type: "LOG_IN", data };
};

export const logoutRequestAction = () => {
  return { type: "LOG_OUT_REQUSET" };
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggingIn: true,
        me: action.data,
      };
    case "LOG_IN_SUCCESS":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: { ...action.data, nickname: "yh" },
      };
    case "LOG_IN_FAILURE":
      return {
        ...state,
        isLoggingIn: true,
        isLoggedIn: false,
        me: action.data,
      };

    case "LOG_OUT_REQUEST":
      return {
        ...state,
        isLoggingOut: false,
      };
    case "LOG_OUT_SUCCESS":
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
        me: null,
      };
    case "LOG_OUT_FAILURE":
      return {
        ...state,
        isLoggingOut: false,
      };
    default:
      return state;
  }
};

export default rootReducer;
